## This script was used to generate smoking and cellular composition covariates from the DNA methylation data 

library(minfi)
library(wateRmelon)
library(FlowSorted.Blood.450k)

library(genefilter)
library("IlluminaHumanMethylation450kanno.ilmn12.hg19")

sampleSheet<-read.csv("SampleSheets/minerva_20160216.csv", stringsAsFactors = FALSE, skip = 8)
sampleSheet<-cbind(paste(sampleSheet$Sentrix_ID, sampleSheet$Sentrix_Position, sep = "_"), sampleSheet)
colnames(sampleSheet)[1]<-"Basename"
idatPath<-paste("idats/", sampleSheet$Sentrix_ID, "/", sampleSheet$Sentrix_ID, "_", sampleSheet$Sentrix_Position, sep ="")
RGSet <- read.450k(idatPath, extended = FALSE)

counts<-estimateCellCounts(RGSet, compositeCellType = "Blood",
                                cellTypes = c("CD8T","CD4T", "NK","Bcell","Mono","Gran"),
                                returnAll = FALSE, meanPlot = FALSE, verbose=TRUE)

								
write.csv(counts, "Analysis/HousemanEstimatedCellCounts.csv")

